import productsActionsTypes from "./products.types";
import axios from "axios";

export const prodcutsListAction = () => async () => {
    try {
        dispatch({type: productsActionsTypes.PRODUCT_LIST_REQUEST })

        const {data} = await axios.get("/api/products")

        dispatch({type: productsActionsTypes.PRODUCT_LIST_SUCCESS, payload: data})
    } catch (error){
        dispatch({type: productsActionsTypes.PRODUCT_LIST_FAIL, payload: error})
    }
}
