export const removeItemFromCart = (cartItems, itemToRemove) => (
    cartItems.filter(cartItem => {
        if (cartItem.id === itemToRemove.id) {
            if (cartItem.color == itemToRemove.color && cartItem.size == itemToRemove.size ) {
                return false
            }
        }
        return true
    })
)
