import React from 'react'
import StripeCheckout from 'react-stripe-checkout';
import axios from "axios";

import fashioDuJour from "../../assets/fashioDuJour.svg"
import "./stripe-button.style.scss";

const StripeButton = ({price}) => {
    const priceForStripe = price * 100;
    const publishableKey = "pk_test_51I72OjKG0ifR7nrtAI3jTkUtJ0x2KcmAXpK7Imx1FwjmSerMwLLAvfPwFWYpBCsQsXMS0k4DtzGOa8RTRIn2byLI00GrIJmOsA";

    const onToken = (token) => {

       axios({
           url: "http://localhost:5000/api/payment/",
           method: "post",
           data: {
               amount: priceForStripe,
               token
           }
       })
       .then(res => {
           console.log("success")
       })
       .catch(err => {
           console.log("error")
       })
    }

    return (
        <StripeCheckout
        label="Checkout"
        name="Fashion Du Jour" 
        description={`Your total is ${price}`}
        image={fashioDuJour}
        ComponentClass="div"
        panelLabel="Pay Mow"
        amount={priceForStripe}
        shippingAddress
        billingAddress
        token={onToken}
        stripeKey={publishableKey} 
        />

    )
}

export default StripeButton
