import {cartActionsTypes} from "./cart.types";

const INITIAL_STATE = {
    cartItems: [],
}

const cartReducer = (state = INITIAL_STATE, action) => {
    switch(action.type) {
        case cartActionsTypes.ADD_ITEM_TO_CART:

            const itemToAdd = action.payload  
            const itemExists = state.cartItems.find(cartItem => cartItem.id === itemToAdd.id && cartItem.color === itemToAdd.color && cartItem.size === itemToAdd.size)

            if (itemExists) {

                return {
                    cartItems: state.cartItems.map(cartItem => {

                        if (cartItem.id === itemToAdd.id && cartItem.color === itemToAdd.color && cartItem.size === itemToAdd.size) {
                            return {...cartItem, qty: cartItem.qty + 1, color: itemToAdd.color, size: itemToAdd.size}

                        } else {
                            return {...cartItem}
                        }

                    })
                }   

            } else {
                return {
                    cartItems: [...state.cartItems, itemToAdd]
                }
            }

        case cartActionsTypes.REMOVE_ITEM_FROM_CART:
            const itemToRemove = action.payload  
            return {cartItems: state.cartItems.filter(cartItem => {
                if (cartItem.id === itemToRemove.id) {
                    if (cartItem.color == itemToRemove.color && cartItem.size == itemToRemove.size ) {
                        return false
                    }
                }
                return true
            })}

        case cartActionsTypes.INCREASE_ITEM_QUANTITY: 
            return {cartItems: state.cartItems.map(cartItem => (
                cartItem.id === action.payload ? {...cartItem, qty: cartItem.qty + 1} : cartItem
            ))}
        default:
            return state
    }
}

export default cartReducer
