import React from 'react'

import "./product.style.scss"
import heart from "../../assets/heart.svg";
import star from "../../assets/star.svg";
import halfStar from "../../assets/half-star.svg";
import gridImage2 from "../../assets/grid-image2.png";
import { prodcutsListAction } from '../../redux/products/products.actions';
import Stars from '../stars/stars.component';

const Product = ({product}) => {
    return (
        <div className="product">
            <div className="image" style={{backgroundImage: `url(${gridImage2})`}}>
                <img className="heart" src={heart}/>
            </div>
            <h3>{product.name}</h3>
            <p><span className="price">{product.price}$</span></p>
            <div className="colors">
                {product.colors.map((color, index) => (
                    <span key={index} className={color}></span>

                ))}
            </div>
            <Stars stars={product.rating}/>
        </div>
    )
}

export default Product
