import {productsActionsTypes} from "./products.types";

const INITIAL_STATE = {
    loading: false,
    products: [],
    error: null,
    productsloaded: false
}

const cat = "any"
const color = "black"
const from = 25
const to = 500

const productsReducer = (state = INITIAL_STATE, action) => {
    switch (action.type) {
        case productsActionsTypes.PRODUCTS_LIST_REQUEST:
            return {loading: true, products: [], productsloaded: false}
        case productsActionsTypes.PRODUCTS_LIST_SUCCESS:
            return {
                loading: false, 
                productsloaded: true,
                products: action.payload.filter(product => {
                    if (product.category === cat || cat === "any") {

                        if (product.discount < to || to === "any") {

                            if (product.colors.includes(color) || color === "any") {
                                return true
                            } else {
                                return false
                            }

                        } else {
                            return false
                        }

                    } else {
                        return false
                    }
                })
            }

        case productsActionsTypes.PRODUCTS_LIST_FAIL:
            return {loading: false, error: action.payload}
        default: 
            return state
    }
}

export default productsReducer
