import React, { Component } from 'react'

import "./shop-page.style.scss";
import Input from '../../components/input/input.component';
import Product from '../../components/product/product.component';
import gridImage2 from "../../assets/grid-image2.png";
import AsideFilter from '../../components/aside-filter/aside-filter.component';
import Button from '../../components/button/button.component';

class ShopPage extends Component {
    state = {
        products: [
            {
                id: 1,
                name: "shoes bla bla ",
                price: 50,
                discount: 25,
                reviews: 4,
                colors: ["red", "blue", "black"],
                backgroundImage: gridImage2
            }
        ]
    }

    addClass(e) {
        document.querySelector(".filter").classList.toggle("mobile");
        console.log("text")
    }

    render() {
        return (
            <main className="shop-page">
                <div className="container">
                    <div className="row">
                        <div className="col-md-3">

                            <AsideFilter/>
                        </div>

                        <div className="products col-md-9">
                            <div className="d-flex justify-content-between align-items-center heading">
                                <h1>Shop</h1>
                                <p><span>200 </span>items found</p>
                            </div>
                            <Button addClass={this.addClass} text="Filters"></Button>
                            <div className="row">
                                {
                                    this.state.products.map(() => (
                                        <div className="col-sm-6 col-lg-4 ">
                                            <Product/>
                                        </div>
                                    ))
                                }
                            </div>
                        </div>
                    </div>
                </div>
            </main>
        )
    }
}

export default ShopPage
