import React, { Component } from 'react';
import { Swiper, SwiperSlide } from 'swiper/react';

import HpProduct from "../hp-product/hp-prodcut.component";

import "./deals.style.scss";
import 'swiper/swiper.scss';

export class Deals extends Component {
    state = {
        products: [
            {
                id: 0,
                price: 68,
                sizes: ["S" , "M", "L", "Xl"],
                addedToWishlist: true,
                backgroundImage: "https://media.gq.com/photos/59af20a43482f876ccb37eee/3:2/w_2396,h_1597,c_limit/gq-suite-guide-thomas-whiteside05.jpg",
                discount: 30
            },
            {
                id: 1,
                price: 68,
                sizes: ["S" , "M", "L", "Xl"],
                addedToWishlist: false,
                backgroundImage: "https://media.gq.com/photos/59af20a43482f876ccb37eee/3:2/w_2396,h_1597,c_limit/gq-suite-guide-thomas-whiteside05.jpg",
                discount: 30
            },
            {
                id: 2,
                price: 68,
                sizes: ["S" , "M", "L", "Xl"],
                addedToWishlist: false,
                backgroundImage: "https://media.gq.com/photos/59af20a43482f876ccb37eee/3:2/w_2396,h_1597,c_limit/gq-suite-guide-thomas-whiteside05.jpg",
                discount: 0
            },
            {
                id: 3,
                price: 68,
                sizes: ["S" , "M", "L", "Xl"],
                addedToWishlist: false,
                backgroundImage: "https://media.gq.com/photos/59af20a43482f876ccb37eee/3:2/w_2396,h_1597,c_limit/gq-suite-guide-thomas-whiteside05.jpg",
                discount: 0
            },
            {
                id: 4,
                price: 68,
                sizes: ["S" , "M", "L", "Xl"],
                addedToWishlist: false,
                backgroundImage: "https://media.gq.com/photos/59af20a43482f876ccb37eee/3:2/w_2396,h_1597,c_limit/gq-suite-guide-thomas-whiteside05.jpg",
                discount: 0
            },
        ]
    }

    render() {
        return (
            <section className="deals">
                <div className="container">
                    <h2>Hot Deals</h2>
                </div>
                <Swiper
                slidesPerView= "auto"
                spaceBetween= {150}
                centeredSlides= {true}
                initialSlide= {2}
                >
                    {this.state.products.map(({id, price, sizes, addedToWishlist, backgroundImage, discount}) => (
                        <SwiperSlide key={id} >
                            <HpProduct 
                            price={price} 
                            sizes={sizes} 
                            addedToWishlist={addedToWishlist} 
                            backgroundImage={backgroundImage}
                            discount={discount}
                            />
                        </SwiperSlide>
                    ))}

                </Swiper>
            </section>
        )
    }
}

export default Deals
