import {productsActionsTypes} from "./products.types";
import axios from "axios";

export const productDetailsAction = (id) => async (dispatch) => {
    try {
        dispatch({type: productsActionsTypes.PRODUCT_DETAILS_REQUEST })

        const {data} = await axios.get(`/api/products/${id}`)

        dispatch({type: productsActionsTypes.PRODUCT_DETAILS_SUCCESS, payload: data})
    } catch (error){
        dispatch({type: productsActionsTypes.PRODUCT_DETAILS_FAIL, payload: error})
    }
}
