import React, {useState} from 'react';
import Post from '../../components/post/post.component';

import "./blog-page.style.scss";
import post from "../../assets/post.png";

const BlogPage = () => {
    const [posts, setPosts] = useState([
        {
            id: 1,
            title: "LAST MINUTE HOLIDAY PREP HACKS!",
            date: "1-1-2020",
            thumbnail: post 
        },
        {
            id: 2,
            title: "LAST MINUTE HOLIDAY PREP HACKS!",
            date: "1-1-2020",
            thumbnail: post 
        },
        {
            id: 3,
            title: "LAST MINUTE HOLIDAY PREP HACKS!",
            date: "1-1-2020",
            thumbnail: post 
        },
        {
            id: 4,
            title: "LAST MINUTE HOLIDAY PREP HACKS!",
            date: "1-1-2020",
            thumbnail: post 
        },
        {
            id: 5,
            title: "LAST MINUTE HOLIDAY PREP HACKS!",
            date: "1-1-2020",
            thumbnail: post 
        },
        {
            id: 6,
            title: "LAST MINUTE HOLIDAY PREP HACKS!",
            date: "1-1-2020",
            thumbnail: post 
        },
    ])

    return (
        <main className="about-page">
            <div className="container">
                <h1>Blog</h1>
                <div className="row mx-0">
                    {
                        posts.slice(0,4).map(({title, date, thumbnail}, idx) => (
                            <Post key={idx} title={title} date={date} thumbnail={thumbnail}/>
                        ))
                    }                        
                </div>  
            </div>
        </main>
    )
}

export default BlogPage
