import React, { Component } from 'react';

import CheckoutProduct from '../../components/checkout-product/checkout-product.component';

import "./checkout-page.style.scss";
import arrowRight from "../../assets/arrow-right.svg";
import arrowLeft from "../../assets/arrow-left.svg";

export class CheckoutPage extends Component {
    state = {
        products: [
            {
                id: 0,
                price: 68,
                sizes: ["S" , "M", "L", "Xl"],
                addedToWishlist: true,
                backgroundImage: "https://media.gq.com/photos/59af20a43482f876ccb37eee/3:2/w_2396,h_1597,c_limit/gq-suite-guide-thomas-whiteside05.jpg",
                discount: 30
            },
            {
                id: 1,
                price: 68,
                sizes: ["S" , "M", "L", "Xl"],
                addedToWishlist: false,
                backgroundImage: "https://media.gq.com/photos/59af20a43482f876ccb37eee/3:2/w_2396,h_1597,c_limit/gq-suite-guide-thomas-whiteside05.jpg",
                discount: 30
            },
            {
                id: 2,
                price: 68,
                sizes: ["S" , "M", "L", "Xl"],
                addedToWishlist: false,
                backgroundImage: "https://media.gq.com/photos/59af20a43482f876ccb37eee/3:2/w_2396,h_1597,c_limit/gq-suite-guide-thomas-whiteside05.jpg",
                discount: 0
            },
            {
                id: 3,
                price: 68,
                sizes: ["S" , "M", "L", "Xl"],
                addedToWishlist: false,
                backgroundImage: "https://media.gq.com/photos/59af20a43482f876ccb37eee/3:2/w_2396,h_1597,c_limit/gq-suite-guide-thomas-whiteside05.jpg",
                discount: 0
            },
            {
                id: 4,
                price: 68,
                size: "S",
                backgroundImage: "https://media.gq.com/photos/59af20a43482f876ccb37eee/3:2/w_2396,h_1597,c_limit/gq-suite-guide-thomas-whiteside05.jpg",
                discount: 0
            },
        ]
    }
    render() {
        return (
            <main className="checkout-page">
                <div className="container">
                    <h1>Checkout</h1>
                    <div className="row checkout-inner">
                        <div className="col-md-3"></div>
                        <div className="col-md-3">
                            <span className="item">Item</span>
                        </div>
                        <div className="col-md-3">
                            <span className="item">Quantity</span>
                        </div>
                        <div className="col-md-3">
                            <span className="item">Price</span>
                        </div>
                    </div>

                    <div className="checkout-products-list">
                        {
                            this.state.products.map(() => (
                                <CheckoutProduct/>
                            ))
                        }
                    </div>
                </div>
            </main>
        )
    }
}

export default CheckoutPage
