import React from 'react';

import Button from "../button/button.component";
import { shrinkifySizes } from '../../utils/utils';
import {Link} from "react-router-dom";

import heart from "../../assets/heart.svg";
import heartRed from "../../assets/heart-red.svg";
import "./hp-product.style.scss";

const HpProduct = ({price, sizes, addedToWishlist, thumb, discount}) => {
    return (
            <Link to="product" className="hp-product" style={{backgroundImage: `url(https://media.gq.com/photos/59af20a43482f876ccb37eee/3:2/w_2396,h_1597,c_limit/gq-suite-guide-thomas-whiteside05.jpg)`}}>
                <div className="overlay"></div>
                <div className="details">
                    <div className="price-wrapper">
                        <span className={`price ${discount !== 0 && "cheaper" }`}>{price}$</span>
                        {discount !== 0 && <span className="discount">{discount}$</span> }

                    </div>

                    <div className="wrapper">
                        <div className="action">
                            <Button text={"Add To Cart"}/>
                        </div>
                        <div className="size">
                            {
                                sizes.map((size, index) => {
                                    return (
                                        <span key={index}>{index !== 0 && " /"} {shrinkifySizes(size)}</span>
                                    )
                                })
                            }
                        </div>
                    </div>
                </div>

                <div className="wishlist-icon">
                    { 
                        addedToWishlist 
                        ?  <img src={heartRed} /> 
                        : <img src={heart} />    
                    }
                </div> 
            </Link>                            
    )
}

export default HpProduct
