import React from 'react';
import {Link} from "react-router-dom";

import "./button.style.scss";

const Button = ({text, addClass, type, icon, link, disabled}) => {
    if (type) {
        return (
            <button onClick={addClass} className="custom-button form" type="submit">{text}</button>
        )
    } else {
        return (
            <Link to={link}  onClick={addClass} className={`custom-button link ${disabled ? "isDisabled" : ""}`}><img src={icon}/>{text}</Link>
        )
    }

}

export default Button
