import React from 'react'
import { Swiper, SwiperSlide } from 'swiper/react';
import SwiperCore, {Navigation} from 'swiper';

import 'swiper/swiper.scss';
import "./for-you.style.scss"; 
import 'swiper/components/navigation/navigation.scss';
import HpProduct from '../hp-product/hp-prodcut.component';
import heart from "../../assets/heart.svg";


SwiperCore.use(Navigation);

class ForYou extends React.Component {
    state = [
        {
            id: 0,
            price: 68,
            sizes: ["S" , "M", "L", "Xl"],
            addedToWishlist: false,
            backgroundImage: "https://media.gq.com/photos/59af20a43482f876ccb37eee/3:2/w_2396,h_1597,c_limit/gq-suite-guide-thomas-whiteside05.jpg"
        },
        {
            id: 1,
            price: 68,
            sizes: ["S" , "M", "L", "Xl"],
            addedToWishlist: false,
            backgroundImage: "https://media.gq.com/photos/59af20a43482f876ccb37eee/3:2/w_2396,h_1597,c_limit/gq-suite-guide-thomas-whiteside05.jpg"
        },
        {
            id: 2,
            price: 68,
            sizes: ["S" , "M", "L", "Xl"],
            addedToWishlist: false,
            backgroundImage: "https://media.gq.com/photos/59af20a43482f876ccb37eee/3:2/w_2396,h_1597,c_limit/gq-suite-guide-thomas-whiteside05.jpg"
        },
        {
            id: 3,
            price: 68,
            sizes: ["S" , "M", "L", "Xl"],
            addedToWishlist: false,
            backgroundImage: "https://media.gq.com/photos/59af20a43482f876ccb37eee/3:2/w_2396,h_1597,c_limit/gq-suite-guide-thomas-whiteside05.jpg"
        },
    ]
    render() {
        return (
            <section className="for-you">
                <div className="container">
                    <div className="for-you-container">
                        <div className="headline">
                            <h2>For You</h2>
                            <span>View all</span>
                        </div>

                        <Swiper
                        spaceBetween={50}
                        slidesPerView={2}
                        slidesPerColumn={2}
                        scrollbar={{ draggable: true }}
                        onSlideChange={() => console.log('slide change')}
                        onSwiper={(swiper) => console.log(swiper)}
                        navigation
                        >
                            {/* {this.state.map(({id, price, sizes, addedToWishlist, backgroundImage}) => ())} */}

                                     <div class="swiper-slide">
                                        <div className="product" style={{backgroundImage: `url(https://media.gq.com/photos/59af20a43482f876ccb37eee/3:2/w_2396,h_1597,c_limit/gq-suite-guide-thomas-whiteside05.jpg)`}}>
                                            <div className="overlay"></div>
                                            <div className="details">
                                                <div className="price-wrapper">
                                                    <span className="price">58$</span>
                                                </div>

                                                <div className="wrapper">
                                                    <div className="action">
                                                        <a href="#" className="button">Add To Cart</a>
                                                    </div>
                                                    <div className="size">
                                                        <span>S </span>
                                                        <span>/ M </span>
                                                        <span>/ X </span>
                                                        <span>/ XL </span>
                                                    </div>
                                                </div>
                                            </div>

                                            <div className="wishlist-icon">
                                                <img src={heart}/>    
                                            </div> 
                                        </div>            
                                    </div>                


                        </Swiper>
                    </div>
                </div>  
            </section>
        )
    }
}

export default ForYou
