import React from 'react'
import { Swiper, SwiperSlide } from 'swiper/react';
import SwiperCore, {Navigation} from 'swiper';

import 'swiper/swiper.scss';
import "./for-you.style.scss"; 
import 'swiper/components/navigation/navigation.scss';
import HpProduct from '../hp-product/hp-prodcut.component';
import heart from "../../assets/heart.svg";


SwiperCore.use(Navigation);

class ForYou extends React.Component {
    state = {
        products: [
            {
                id: 0,
                price: 68,
                sizes: ["S" , "M", "L", "Xl"],
                addedToWishlist: true,
                backgroundImage: "https://media.gq.com/photos/59af20a43482f876ccb37eee/3:2/w_2396,h_1597,c_limit/gq-suite-guide-thomas-whiteside05.jpg",
                discount: 30
            },
            {
                id: 1,
                price: 68,
                sizes: ["S" , "M", "L", "Xl"],
                addedToWishlist: false,
                backgroundImage: "https://media.gq.com/photos/59af20a43482f876ccb37eee/3:2/w_2396,h_1597,c_limit/gq-suite-guide-thomas-whiteside05.jpg",
                discount: 30
            },
            {
                id: 2,
                price: 68,
                sizes: ["S" , "M", "L", "Xl"],
                addedToWishlist: false,
                backgroundImage: "https://media.gq.com/photos/59af20a43482f876ccb37eee/3:2/w_2396,h_1597,c_limit/gq-suite-guide-thomas-whiteside05.jpg",
                discount: 0
            },
            {
                id: 3,
                price: 68,
                sizes: ["S" , "M", "L", "Xl"],
                addedToWishlist: false,
                backgroundImage: "https://media.gq.com/photos/59af20a43482f876ccb37eee/3:2/w_2396,h_1597,c_limit/gq-suite-guide-thomas-whiteside05.jpg",
                discount: 0
            },
            {
                id: 4,
                price: 68,
                sizes: ["S" , "M", "L", "Xl"],
                addedToWishlist: false,
                backgroundImage: "https://media.gq.com/photos/59af20a43482f876ccb37eee/3:2/w_2396,h_1597,c_limit/gq-suite-guide-thomas-whiteside05.jpg",
                discount: 0
            },
        ]
    }
    setSectionHeight() {
        const slideHeight =  document.querySelector(".swiper-slide").getBoundingClientRect().height
        document.querySelector(".for-you").style.hegith = `${(slideHeight*2) + 150}px`
        console.log(slideHeight)
    }
    componentDidMount() {
        setSectionHeight()
    }


    render() {
        return (
            <section className="for-you">
                <div className="container">
                    <div className="for-you-container">
                        <div className="headline">
                            <h2>For You</h2>
                            <a href="#">View all</a>
                        </div>

                        <Swiper
                        spaceBetween={50}
                        slidesPerView={1}
                        slidesPerColumn={1}
                        scrollbar={{ draggable: true }}
                        navigation
                        breakpoints={{
                            768: {
                                slidesPerView: 2,
                                slidesPerColumn: 2
                            }
                          }}
                        >
                            {this.state.products.map(({id, price, sizes, addedToWishlist, backgroundImage, discount}) => (
                                <SwiperSlide key={id} >
                                    <HpProduct 
                                    price={price} 
                                    sizes={sizes} 
                                    addedToWishlist={addedToWishlist} 
                                    backgroundImage={backgroundImage}
                                    discount={discount}
                                    />
                                </SwiperSlide>
                            ))}

                        </Swiper>
                    </div>
                </div>  
            </section>
        )
    }
}

export default ForYou
