import React, { Component } from 'react';

import CartProduct from '../../components/cart-product/cart-product.component';
import Button from '../../components/button/button.component';

import "./cart-page.style.scss";

export class CartPage extends Component {
    state = {
        products: [
            {
                id: 0,
                title: "Shoes bla bla",
                price: 68,
                size: "MEDIUM",
                color: "black",
                backgroundImage: "https://media.gq.com/photos/59af20a43482f876ccb37eee/3:2/w_2396,h_1597,c_limit/gq-suite-guide-thomas-whiteside05.jpg",
            },
            {
                id: 1,
                title: "Shoes bla bla",
                price: 68,
                size: "SMALL",
                color: "blue",
                backgroundImage: "https://media.gq.com/photos/59af20a43482f876ccb37eee/3:2/w_2396,h_1597,c_limit/gq-suite-guide-thomas-whiteside05.jpg",
            },
            {
                id: 2,
                title: "Shoes bla bla",
                price: 68,
                size: "X-lARGE",
                color: "red",
                backgroundImage: "https://media.gq.com/photos/59af20a43482f876ccb37eee/3:2/w_2396,h_1597,c_limit/gq-suite-guide-thomas-whiteside05.jpg",
            },

        ]
    }
    render() {
        return (
            <main className="checkout-page">
                <div className="container">
                    <h1>Checkout</h1>
                    <div className="row checkout-inner d-none d-md-flex">
                        <div className="col-md-3"></div>
                        <div className="col-md-3">
                            <span className="item">Item</span>
                        </div>
                        <div className="col-md-3">
                            <span className="item">Quantity</span>
                        </div>
                        <div className="col-md-3">
                            <span className="item">Price</span>
                        </div>
                    </div>

                    <div className="checkout-products-list">
                        {
                            this.state.products.map(({id, title, price, color, size, backgroundImage}) => (
                                <CartProduct key={id} title={title} price={price} color={color} size={size} backgroundImage={backgroundImage}/>
                            ))
                        }
                    </div>

                    <div className="pay text-right">
                        <p className="total-price">Total: 40$</p>
                        <Button text="Checkout"/>
                    </div>
                </div>
            </main>
        )
    }
}

export default CartPage
