import React from 'react'
import { Swiper, SwiperSlide } from 'swiper/react';
import SwiperCore, {Navigation} from 'swiper';

import 'swiper/swiper.scss';
import "./for-you.style.scss"; 
import 'swiper/components/navigation/navigation.scss';
import heart from "../../assets/heart.svg";
import product from "../../assets/product.png";

SwiperCore.use(Navigation);

const ForYou = () => {
    return (
        <section className="for-you">
            <div className="container">
                <div className="for-you-container">
                    <div className="headline">
                        <h2>For You</h2>
                        <span>View all</span>
                    </div>

                    <Swiper
                    spaceBetween={50}
                    slidesPerView={2}
                    slidesPerColumn={2}
                    scrollbar={{ draggable: true }}
                    onSlideChange={() => console.log('slide change')}
                    onSwiper={(swiper) => console.log(swiper)}
                    navigation
                    >
                        <SwiperSlide>
                            <div className="product" style={{backgroundImage: `url(https://media.gq.com/photos/59af20a43482f876ccb37eee/3:2/w_2396,h_1597,c_limit/gq-suite-guide-thomas-whiteside05.jpg)`}}>
                                <div className="overlay"></div>
                                <div className="details">
                                    <div className="price-wrapper">
                                        <span className="price">68$</span>
                                    </div>

                                    <div className="wrapper">
                                        <div className="action">
                                            <a href="#" className="button">Add To Cart</a>
                                        </div>
                                        <div className="size">
                                            <span>S </span>
                                            <span>/ M </span>
                                            <span>/ X </span>
                                            <span>/ XL </span>
                                        </div>
                                    </div>

                                    <div className="wishlist-icon">
                                        <img src={heart}/>    
                                    </div>   
                                </div> 
                            </div>                            
                        </SwiperSlide>
                    </Swiper>
                </div>
            </div>  
        </section>
    )
}

export default ForYou
