import React, {useEffect} from 'react'
import { Swiper, SwiperSlide } from 'swiper/react';
import SwiperCore, {Navigation} from 'swiper';
import {useDispatch, useSelector} from "react-redux";

import HpProduct from '../hp-product/hp-prodcut.component';
import { productsListAction } from '../../redux/products/products.actions';

import 'swiper/swiper.scss';
import "./for-you.style.scss"; 
import 'swiper/components/navigation/navigation.scss';
import heart from "../../assets/heart.svg";


SwiperCore.use(Navigation);

const ForYou = () => {

    const productsList = useSelector(state => state.productsList)
    const {loading, error, products, loaded} = productsList

    const dispatch = useDispatch()
    useEffect(() => {

        dispatch(productsListAction())
        console.log('2')

        if (products.length > 1) {
            const mediaQuery = window.matchMedia('(min-width: 768px)')
            const slideHeight =  document.querySelector(".swiper-slide").getBoundingClientRect().height
            if (mediaQuery.matches) {
                document.querySelector(".for-you").style.height = `${(slideHeight*2) + 200}px`
            } else {
                document.querySelector(".for-you").style.height = `${slideHeight + 100}px`
            }
        }

    }, [dispatch]) 
    return (
        <section className="for-you">
            <div className="container">
                <div className="for-you-container">
                    <div className="headline">
                        <h2>For You</h2>
                        <a href="#">View all</a>
                    </div>

                    <Swiper
                    spaceBetween={50}
                    slidesPerView={1}
                    slidesPerColumn={1}
                    scrollbar={{ draggable: true }}
                    navigation
                    breakpoints={{
                        768: {
                            slidesPerView: 2,
                            slidesPerColumn: 2
                        }
                        }}
                    >
                        {products.map(({id, price, sizes, addedToWishlist, thumb, discount}) => (
                            <SwiperSlide key={id} >
                                <HpProduct 
                                price={price} 
                                sizes={sizes} 
                                addedToWishlist={addedToWishlist} 
                                thumb={thumb}
                                discount={discount}
                                />
                            </SwiperSlide>
                        ))}

                    </Swiper>
                </div>
            </div>  
        </section>
    )
}

export default ForYou
