import React, { Component } from 'react';

import Button from "../button/button.component";
import Post from '../post/post.component';

import "./hp-blog.style.scss";
import post from "../../assets/post.png";

export class HpBlog extends Component {
    state = {
        posts: [
            {
                id: 1,
                title: "LAST MINUTE HOLIDAY PREP HACKS!",
                date: "1-1-2020",
                thumbnail: post 
            },
            {
                id: 2,
                title: "LAST MINUTE HOLIDAY PREP HACKS!",
                date: "1-1-2020",
                thumbnail: post 
            },
            {
                id: 3,
                title: "LAST MINUTE HOLIDAY PREP HACKS!",
                date: "1-1-2020",
                thumbnail: post 
            },
            {
                id: 4,
                title: "LAST MINUTE HOLIDAY PREP HACKS!",
                date: "1-1-2020",
                thumbnail: post 
            },
            {
                id: 5,
                title: "LAST MINUTE HOLIDAY PREP HACKS!",
                date: "1-1-2020",
                thumbnail: post 
            },
            {
                id: 6,
                title: "LAST MINUTE HOLIDAY PREP HACKS!",
                date: "1-1-2020",
                thumbnail: post 
            },
        ]
    }
    render() {
        return (
            <section className="hp-blog">
                <div className="container">
                    <h2>Latest News</h2>
                    <div className="row mx-0">
                        {
                            this.state.posts.slice(0,4).map(({title, date, thumbnail}) => (
                                <Post title={title} date={date} thumbnail={thumbnail}/>
                            ))
                        }                        
                        <div className="btn-wrapper text-center w-100">
                            <Button text="View More"/>
                        </div>
                    </div>
                </div>
            </section>
        )
    }
}

export default HpBlog
