import {cartActionsTypes} from "./cart.types";

const INITIAL_STATE = {
    cartItems: [],
}

const cartReducer = (state = INITIAL_STATE, action) => {
    switch(action.type) {
        case cartActionsTypes.ADD_ITEM_TO_CART:
            const itemExists = state.cartItems.find(cartItem => cartItem.id === action.payload.id && cartItem.color === action.payload.color && cartItem.size === action.payload.size)
            if (itemExists) {
                console.log("itemEXISTS")
                return {
                    cartItems: state.cartItems.map(cartItem => {
                        if (cartItem.id === action.payload.id && cartItem.color === action.payload.color && cartItem.size === action.payload.size) {
                            console.log("item EXISTS case 1")
                            return {...cartItem, qty: cartItem.qty + 1, color: action.payload.color, size: action.payload.size}

                        } else {
                            console.log("item EXISTS case 2")
                            return {...cartItem}
                        }
                    })
                }                
            } else {
                console.log("item DOSENT EXISTS")
                return {
                    cartItems: [...state.cartItems, action.payload]
                }
            }

        case cartActionsTypes.REMOVE_ITEM_FROM_CART:
            return {cartItems: state.cartItems.filter(cartItem => cartItem.id != action.payload)}
        default:
            return state
    }
}

export default cartReducer
