import React, {useState, useEffect} from 'react'
import {useDispatch, useSelector} from "react-redux";

import "./shop-page.style.scss";
import Product from '../../components/product/product.component';
import gridImage2 from "../../assets/grid-image2.png";
import AsideFilter from '../../components/aside-filter/aside-filter.component';
import Button from '../../components/button/button.component';
import { prodcutsListAction } from '../../redux/products/products.actions';

const ShopPage = () => {

    const productsList = useSelector(state => state.productsList)
    const {loading, error, products} = productsList

    const dispatch = useDispatch()
    useEffect(() => {
        dispatch(prodcutsListAction())
    }, [dispatch])

    const addClass = (e) => {
        document.querySelector(".filter").classList.toggle("mobile");
        console.log("text")
    }

    return (
        <main className="shop-page">
            <div className="container">
                <div className="row">

                    <div className="products col-md-9">

                        <div className="row">
                            {
                                products.map((product) => (
                                    <div className="col-sm-6 col-lg-4 ">
                                        <Product key={product._id} product={product}/>
                                    </div>
                                ))
                            }
                        </div>
                    </div>
                </div>
            </div>
        </main>
        )
    }

export default ShopPage
