import { 
    removeItemFromCart,
    addItemToCart
} from "./cart.utils.js";
import {cartActionsTypes} from "./cart.types";
import { increaseItemQuantity } from "./cart.actions.js";

const INITIAL_STATE = {
    cartItems: [],
}

const cartReducer = (state = INITIAL_STATE, action) => {
    switch(action.type) {
        case cartActionsTypes.ADD_ITEM_TO_CART:
            return {cartItems: addItemToCart(state.cartItems, action.payload)}

        case cartActionsTypes.REMOVE_ITEM_FROM_CART:
            return {cartItems: removeItemFromCart(state.cartItems, action.payload)}

        case cartActionsTypes.INCREASE_ITEM_QUANTITY: 
            return {cartItems: increaseItemQuantity(cartItems, action.payload)}

        case cartActionsTypes.DECREASE_ITEM_QUANTITY:
            const itemToDecrease = action.payload
            return {cartItems: state.cartItems.map(cartItem => (
                cartItem.id === itemToDecrease.id && cartItem.color === itemToDecrease.color && cartItem.size === itemToDecrease.size ? {...cartItem, qty: cartItem.qty - 1} : cartItem
            ))}

        default:
            return state
    }
}

export default cartReducer
