import React from 'react';

import Button from "../button/button.conponent";

import heart from "../../assets/heart.svg";
import heartRed from "../../assets/heart-red.svg";
import "./hp-product.style.scss";

const HpProduct = ({price, sizes, addedToWishlist, backgroundImage, discount}) => {
    return (
        <div className="product" style={{backgroundImage: `url(${backgroundImage})`}}>
            <div className="overlay"></div>
            <div className="details">
                <div className="price-wrapper">
                    {discount !== 0 && <span className="discount">{discount}$</span> }
                    <span className={`price ${discount !== 0 && "cheaper" }`}>{price}$</span>

                </div>

                <div className="wrapper">
                    <div className="action">
                        <Button text={"Add To Cart"}/>
                    </div>
                    <div className="size">
                        <span>S </span>
                        <span>/ M </span>
                        <span>/ X </span>
                        <span>/ XL </span>
                    </div>
                </div>
            </div>

            <div className="wishlist-icon">
                { 
                    addedToWishlist 
                    ?  <img src={heartRed} /> 
                    : <img src={heart} />    
                }
            </div> 
        </div>                            
    )
}

export default HpProduct
