export const removeItem = (cartItems, itemToRemove) => {
    cartItems.map(cartItem => {
        if (cartItem.id === itemToRemove.id) {
            if (cartItem.color == itemToRemove.color && cartItem.size == itemToRemove.size ) {
                console.log(cartItems)
            }
        }
        console.log("s")
    })
}

