import React, {useEffect} from 'react';
import {useDispatch, useSelector} from "react-redux";

import Input from "../input/input.component";
import Button from "../button/button.component";
import { getCats } from '../../redux/categories/categories.actions';

import "./aside-filter.style.scss";
import { Link } from 'react-router-dom';

const AsideFilter = ({catToFilter, colorToFilter, fromToFilter, toToFilter}) => {

    const cat = useSelector(state => state.cat);
    const {categories} = cat;

    const dispatch = useDispatch()

    useEffect(() => {
        dispatch(getCats())
    }, [dispatch]) 

    const addClass = (e) => {
        document.querySelector(".filter").classList.toggle("mobile");
        console.log("text")
    }

    const mediaQuery = window.matchMedia('(max-width: 768px)')

    return (
        <aside className="filter">
            <Button addClass={addClass} text="cancel"></Button>
            <div className="cats">
                <h2>Categories</h2>
                <div className="cats-inner">
                    {
                        categories.map(({name, _id}) => (
                            <h3 key={_id}><Link to={`/shop/${{_id}}/${colorToFilter}/${fromToFilter}/${toToFilter}`}>{name}</Link></h3>
                        ))
                    }
                </div>
            </div>

            <div className="price">
                <h2>Price</h2>
                <div className="price-inner">
                    <Input type="text" placeholder="From"/>
                    <span className="dash">-</span> 
                    <Input type="text" placeholder="To"/> 
                </div>
            </div>

            <div className="color">
                <h2>Color</h2>
                <span className="color black"></span>
                <span className="color white"></span>
                <span className="color gray"></span>
                <span className="color red"></span>
                <span className="color blue"></span>
                <span className="color green"></span>
                <span className="color yellow"></span>
            </div>

            <Button addClass={addClass} text="cancel"></Button>

            {/*
            ONLY IF THE SHOP IS FOR BOTH GENDERS 
            <div className="types">
                <p className="type men">Men</p>
                <p className="type women">Women</p>
            </div> */}
        </aside>
    )
}

export default AsideFilter
