import React from 'react';

import "./button.style.scss";

const Button = ({text, addClass, type, icon}) => {
    if (type) {
        return (
            <button onClick={addClass} className="custom-button form" type="submit">{text}</button>
        )
    } else {
        return (
            <a href="#" onClick={addClass} className="custom-button link"><img src={icon}/>{text}</a>
        )
    }

}

export default Button
