    const priceForStripe = price * 100;
    const publishableKey = "pk_live_GcROQaKvN9fxzmzliPHwv4Bk001VkSpsPq";

    const onToken = (token) => {
        console.log(token)
    }

    return (
        <StripeCheckout
        label="Pay Now"
        name="Fashion Du Jour" 
        description={`Your total is ${price}`}
        image={fashioDuJour}
        ComponentClass="div"
        panelLabel="Pay Mow"
        amount={priceForStripe}
        shippingAddress
        billingAddress
        token={onToken}
        stripeKey={publishableKey} 
        />

    )
}

export default StripeButton
