export const shrinkifySizes = (size) => {
    if (size === "small") {
        size = "S"
        return size
    } else if (size === "medium") {
        size = "M"
        return size
    } else if (size === "large") {
        size = "L"
        return size
    } else if (size === "x large") {
        size = "XL"
        return size
    }
}
