import {cartActionsTypes} from "./cart.types";

const INITIAL_STATE = {
    cartItems: [],
}

const cartReducer = (state = INITIAL_STATE, action) => {
    switch(action.type) {
        case cartActionsTypes.ADD_ITEM_TO_CART:
            // const itemExists = state.cartItems.find(cartItem => cartItem.id === action.payload.id)
            // if (itemExists) {
            //     return {
            //         cartItems: state.cartItems.map(cartItem => {
            //             cartItem.id === itemExists.id ? {...cartItem, qty: 2} : ""
            //         })
            //     }                
            // } else {
            //     return {
            //         cartItems: [...state.cartItems, action.payload]
            //     }
            // }
        case cartActionsTypes.REMOVE_ITEM_FROM_CART:
            return {cartItems: state.cartItems.filter(cartItem => cartItem.id != action.payload)}
        default:
            return state
    }
}

export default cartReducer
